import { BehaviorSubject, Observable, Subject } from 'rxjs';
import { map } from 'rxjs/operators';
import { BaseEvent, LockEvent } from './interfaces/event.interface';
import { entityProperty as _entityProperty, ID, log as _log, LOG_LEVEL, UID, withLog as _withLog } from './utils/tool-kit.util';
import { HashMap } from './utils/types';

export type BaseEntityType = 'node' | 'link' | 'port' | 'point';

export class BaseEntity {
	protected _id: ID;
	/**
	 * a prefix to make logs more easier
	 */
	protected _destroyed = new Subject<void>();
	protected _destroyed$ = this._destroyed.asObservable();
	protected _locked = new BehaviorSubject(false);
	protected _locked$ = this._locked.pipe(
		this.entityPipe('locked'),
		map(locked => new LockEvent(this, locked))
	);

	protected readonly _logPrefix: string;

	constructor(id?: ID, logPrefix = '') {
		this._id = id || UID();
		this._logPrefix = `${logPrefix}`;
	}

	public get id(): ID {
		return this._id;
	}

	public set id(id: ID) {
		this._id = id;
	}

	log(message: string, ...args: any): void {
		_log(`${this._logPrefix} ${message}: `, LOG_LEVEL.LOG, ...args);
	}

	withLog(message: string, ...args: any): any {
		return _withLog(`${this._logPrefix} ${message}: `, LOG_LEVEL.LOG, ...args);
	}

	entityPipe(logMessage: string = '') {
		return _entityProperty(this.onEntityDestroy(), 0, `${this._logPrefix}: ${logMessage}`);
	}

	getLocked(): boolean {
		return this._locked.getValue();
	}

	setLocked(locked: boolean = true) {
		this._locked.next(locked);
	}

	// eslint-disable-next-line
	doClone(lookupTable: HashMap<any> = {}, clone: any) {
		/*noop*/
	}

	public clone(lookupTable: HashMap<any> = {}) {
		// try and use an existing clone first
		if (lookupTable[this.id]) {
			return lookupTable[this.id];
		}
		const clone = { ...this };
		clone.id = UID();
		// clone.clearListeners();
		lookupTable[this.id] = clone;

		this.doClone(lookupTable, clone);
		return clone;
	}

	public serialize() {
		return {
			id: this.id,
			locked: this.getLocked(),
		};
	}

	public lockChanges(): Observable<LockEvent> {
		return this._locked$;
	}

	public destroy() {
		this.log('entity destroyed');
		this._destroyed.next();
		this._destroyed.complete();
	}

	public onEntityDestroy(): Observable<BaseEvent<BaseEntity>> {
		return this._destroyed$.pipe(map(opts => new BaseEvent(this, opts)));
	}
}

import { BaseEntity } from '../base.entity';
import { ID, UID } from '../utils/tool-kit.util';

// region events
export class BaseEvent<T extends BaseEntity> {
	entity: T;
	entityId: ID;
	stopPropagation: () => any;
	firing: boolean;
	id: ID;
	propogate: boolean;

	constructor(entity: T, options?: any) {
		this.id = UID();
		this.entity = entity;
		this.entityId = entity.id;
		this.firing = true;
		this.stopPropagation = () => (this.firing = false);
		this.propogate = options ? options.propagate : null;
	}
}
export class LockEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	locked: boolean;

	constructor(entity: T, locked: boolean = false) {
		super(entity);
		this.locked = locked;
	}
}
export class ParentChangeEvent<P extends BaseEntity = BaseEntity, T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	parent: P;

	constructor(entity: T, parent: P) {
		super(entity);
		this.parent = parent;
	}
}
export class SelectionEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	isSelected: boolean;

	constructor(entity: T, selected: boolean) {
		super(entity);
		this.isSelected = selected;
	}
}
export class PaintedEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	isPainted: boolean;

	constructor(entity: T, painted: boolean = false) {
		super(entity);
		this.isPainted = painted;
	}
}
// endregion

import { BaseEntity } from '../base.entity';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { PaintedEvent, ParentChangeEvent, SelectionEvent } from '../interfaces/event.interface';

export class BaseModel<X extends BaseEntity = BaseEntity> extends BaseEntity {
	protected readonly _type: string;
	protected readonly _parent$: BehaviorSubject<X> = new BehaviorSubject(null);
	protected readonly _selected$: BehaviorSubject<boolean> = new BehaviorSubject(false);
	protected readonly _painted$: BehaviorSubject<boolean> = new BehaviorSubject(false);
	protected readonly _hovered$: BehaviorSubject<boolean> = new BehaviorSubject(false);

	protected readonly parent$: Observable<ParentChangeEvent<X>> = this._parent$.pipe(
		this.entityPipe('ParentsChange'),
		map(p => new ParentChangeEvent<X>(this, p))
	);
	protected readonly selected$: Observable<SelectionEvent>;
	protected readonly painted$: Observable<PaintedEvent>;
	protected readonly hovered$: Observable<boolean>;

	constructor(type?: string, id?: string, logPrefix = '[Base]') {
		super(id, logPrefix);
		this._type = type;
		this.selected$ = this._selected$.pipe(
			this.entityPipe('SelectedChange'),
			map(s => new SelectionEvent(this, s))
		);

		this.painted$ = this._painted$.pipe(
			this.entityPipe('PaintedChange'),
			map(p => new PaintedEvent(this, p))
		);

		this.hovered$ = this._hovered$.pipe(this.entityPipe('HoveredChange'));
	}

	serialize() {
		return {
			...super.serialize(),
			type: this.getType(),
		};
	}

	getParent(): X {
		return this._parent$.getValue();
	}

	setParent(parent: X): void {
		this._parent$.next(parent);
	}

	parentChanges(): Observable<ParentChangeEvent<X>> {
		return this.parent$;
	}

	getPainted(): boolean {
		return this._painted$.getValue();
	}

	setPainted(painted: boolean = true) {
		this._painted$.next(painted);
	}

	getHovered(): boolean {
		return this._hovered$.getValue();
	}

	setHovered(painted: boolean = true) {
		this._hovered$.next(painted);
	}

	selectHovered() {
		return this.hovered$;
	}

	paintChanges(): Observable<PaintedEvent> {
		return this.painted$;
	}

	getType(): string {
		return this._type;
	}

	getSelected(): boolean {
		return this._selected$.getValue();
	}

	selectSelected(): Observable<boolean> {
		return this.selected$.pipe(map(e => e.isSelected));
	}

	setSelected(selected: boolean = true) {
		this._selected$.next(selected);
	}

	selectionChanges(): Observable<SelectionEvent> {
		return this.selected$;
	}

	getSelectedEntities(): BaseModel[] {
		return this._selected$.value ? [this] : [];
	}
}

import { ViewContainerRef, ComponentRef, ComponentFactoryResolver, ComponentFactory, Renderer2 } from '@angular/core';
import { AbstractLabelFactory } from '../../factories/label.factory';
import { DefaultLabelComponent } from '../components/default-label/default-label.component';
import { DefaultLabelModel } from '../models/default-label.model';

export class DefaultLabelFactory extends AbstractLabelFactory<DefaultLabelModel> {
	constructor(protected resolver: ComponentFactoryResolver, protected renderer: Renderer2) {
		super('default');
	}

	generateWidget(label: DefaultLabelComponent, labelHost: ViewContainerRef): ComponentRef<DefaultLabelComponent> {
		const componentRef = labelHost.createComponent(this.getRecipe());

		// attach coordinates and default positional behaviour to the generated component host
		const rootNode = componentRef.location.nativeElement;

		// default style for link
		this.renderer.setStyle(rootNode, 'position', 'absolute');

		// data attributes
		this.renderer.setAttribute(rootNode, 'data-labelid', label.id);

		// on destroy make sure to destroy the componentRef
		label.onEntityDestroy().subscribe(() => {
			componentRef.destroy();
		});

		// assign all passed properties to node initialization.
		Object.entries(label).forEach(([key, value]) => {
			componentRef.instance[key] = value;
		});

		componentRef.instance.setPainted(true);
		return componentRef;
	}

	getRecipe(): ComponentFactory<DefaultLabelComponent> {
		return this.resolver.resolveComponentFactory(DefaultLabelComponent);
	}

	getNewInstance() {
		return new DefaultLabelModel();
	}
}

