import { DiagramEngine } from '../services/engine.service';
import * as PF from 'pathfinding';

export const ROUTING_SCALING_FACTOR = 10;

export class PathFinding {
	private pathFinderInstance:
		| PF.JPFNeverMoveDiagonally
		| PF.JPFAlwaysMoveDiagonally
		| PF.JPFMoveDiagonallyIfNoObstacles
		| PF.JPFMoveDiagonallyIfAtMostOneObstacle;

	constructor(private diagramEngine: DiagramEngine, heuristic = PF.Heuristic.manhattan) {
		this.pathFinderInstance = new PF.AStarFinder({
			heuristic,
			diagonalMovement: PF.DiagonalMovement.Always,
			weight: 0,
		});
	}

	/**
	 * Taking as argument a fully unblocked walking matrix, this method
	 * finds a direct path from point A to B.
	 */
	calculateDirectPath(
		from: {
			x: number;
			y: number;
		},
		to: {
			x: number;
			y: number;
		}
	): number[][] {
		const matrix = this.diagramEngine.getCanvasMatrix();
		const grid = new PF.Grid(matrix);

		const fromX = this.diagramEngine.translateRoutingX(Math.floor(from.x / ROUTING_SCALING_FACTOR));
		const toX = this.diagramEngine.translateRoutingX(Math.floor(to.x / ROUTING_SCALING_FACTOR));
		const fromY = this.diagramEngine.translateRoutingX(Math.floor(from.y / ROUTING_SCALING_FACTOR));
		const toY = this.diagramEngine.translateRoutingX(Math.floor(to.y / ROUTING_SCALING_FACTOR));

		const path = this.pathFinderInstance.findPath(fromX, fromY, toX, toY, grid);
		return path;
	}

	/**
	 * Using @link{#calculateDirectPath}'s result as input, we here
	 * determine the first walkable point found in the matrix that includes
	 * blocked paths.
	 */
	calculateLinkStartEndCoords(
		matrix: number[][],
		path: number[][]
	): {
		start: {
			x: number;
			y: number;
		};
		end: {
			x: number;
			y: number;
		};
		pathToStart: number[][];
		pathToEnd: number[][];
	} {
		const startIndex = path.findIndex(point => matrix[point[1]][point[0]] === 0);
		const endIndex =
			path.length -
			1 -
			path
				.slice()
				.reverse()
				.findIndex(point => matrix[point[1]][point[0]] === 0);

		// are we trying to create a path exclusively through blocked areas?
		// if so, let's fallback to the linear routing
		if (startIndex === -1 || endIndex === -1) {
			return undefined;
		}

		const pathToStart = path.slice(0, startIndex);
		const pathToEnd = path.slice(endIndex);

		return {
			start: {
				x: path[startIndex][0],
				y: path[startIndex][1],
			},
			end: {
				x: path[endIndex][0],
				y: path[endIndex][1],
			},
			pathToStart,
			pathToEnd,
		};
	}

	/**
	 * Puts everything together: merges the paths from/to the centre of the ports,
	 * with the path calculated around other elements.
	 */
	calculateDynamicPath(
		routingMatrix: number[][],
		start: {
			x: number;
			y: number;
		},
		end: {
			x: number;
			y: number;
		},
		pathToStart: number[][],
		pathToEnd: number[][]
	) {
		// generate the path based on the matrix with obstacles
		const grid = new PF.Grid(routingMatrix);
		const dynamicPath = this.pathFinderInstance.findPath(start.x, start.y, end.x, end.y, grid);

		// aggregate everything to have the calculated path ready for rendering
		const pathCoords = pathToStart
			.concat(dynamicPath, pathToEnd)
			.map(coords => [this.diagramEngine.translateRoutingX(coords[0], true), this.diagramEngine.translateRoutingY(coords[1], true)]);

		return PF.Util.compressPath(pathCoords);
	}
}

import { BaseModel } from './base.model';
import { LinkModel } from './link.model';
import { BehaviorSubject, Observable } from 'rxjs';
import { Coords } from '../interfaces/coords.interface';
import { map } from 'rxjs/operators';
import { SerializedPointModel } from '../interfaces/serialization.interface';

export class PointModel extends BaseModel<LinkModel> {
	protected readonly _coords: BehaviorSubject<Coords>;
	protected readonly coords$: Observable<Coords>;

	constructor(link: LinkModel, { x, y }: Coords, id?: string, logPrefix: string = '[Point]') {
		super(link.getType(), id, logPrefix);
		this._coords = new BehaviorSubject<Coords>({ x, y });
		this.coords$ = this._coords.pipe(this.entityPipe('coords'));
		this.setParent(link);
	}

	serialize(): SerializedPointModel {
		return {
			...super.serialize(),
			coords: this.getCoords(),
		};
	}

	isConnectedToPort() {
		return this.getParent().getPortForPoint(this) !== null;
	}

	getLink(): LinkModel {
		return this.getParent();
	}

	destroy() {
		if (this.getParent) {
			this.getParent().removePoint(this);
		}

		super.destroy();
	}

	setCoords(newCoords: Partial<Coords>) {
		this._coords.next({ ...this._coords.getValue(), ...newCoords });
	}

	selectCoords(): Observable<Coords> {
		return this.coords$;
	}

	getCoords(): Coords {
		return this._coords.getValue();
	}

	selectX(): Observable<number> {
		return this.selectCoords().pipe(map(c => c.x));
	}

	selectY(): Observable<number> {
		return this.selectCoords().pipe(map(c => c.y));
	}
}

import { BehaviorSubject, Observable } from 'rxjs';
import { distinctUntilChanged, shareReplay, takeUntil } from 'rxjs/operators';
import { HashMap } from '../utils/types';
import { BaseModel } from './base.model';
import { LinkModel } from './link.model';
import { NodeModel } from './node.model';

export class PortModel extends BaseModel<NodeModel> {
	// TODO: convert all primitives to subjects
	protected name: string;
	protected maximumLinks: number;
	protected linkType: string;

	protected _links$ = new BehaviorSubject<HashMap<LinkModel>>({});
	protected _x$ = new BehaviorSubject(0);
	protected _y$ = new BehaviorSubject(0);
	protected _magnetic$ = new BehaviorSubject(true);
	protected _width$ = new BehaviorSubject(0);
	protected _height$ = new BehaviorSubject(0);
	protected _canCreateLinks$ = new BehaviorSubject(true);

	protected links$ = this._links$.pipe(takeUntil(this.onEntityDestroy()), distinctUntilChanged(), shareReplay(1));
	protected x$ = this._x$.pipe(this.entityPipe('x'));
	protected y$ = this._y$.pipe(this.entityPipe('y'));
	protected magnetic$ = this._magnetic$.pipe(this.entityPipe('magnetic'));

	constructor(
		name: string,
		type?: string,
		id?: string,
		maximumLinks?: number,
		linkType?: string,
		magnetic: boolean = true,
		logPrefix: string = '[Port]'
	) {
		super(type, id, logPrefix);
		this.name = name;
		this.maximumLinks = maximumLinks;
		this.linkType = linkType;
		this.setMagnetic(magnetic);
	}

	serialize() {
		return {
			...super.serialize(),
			name: this.getName(),
			linkType: this.getLinkType(),
			maximumLinks: this.getMaximumLinks(),
			type: this.getType(),
			magnetic: this.getMagnetic(),
			height: this.getHeight(),
			width: this.getWidth(),
			canCreateLinks: this.getCanCreateLinks(),
			...this.getCoords(),
		};
	}

	getNode() {
		return this.getParent();
	}

	getName() {
		return this.name;
	}

	getCanCreateLinks(): boolean {
		const numberOfLinks: number = Object.keys(this._links$.getValue()).length;

		if (this.maximumLinks && numberOfLinks >= this.maximumLinks) {
			return false;
		}

		return this._canCreateLinks$.getValue();
	}

	getCoords() {
		return { x: this.getX(), y: this.getY() };
	}

	selectCanCreateLinks() {
		return this._canCreateLinks$;
	}

	setCanCreateLinks(value: boolean) {
		this._canCreateLinks$.next(value);
	}

	getMagnetic() {
		return this._magnetic$.getValue();
	}

	selectMagnetic() {
		return this.magnetic$;
	}

	setMagnetic(magnetic: boolean) {
		this._magnetic$.next(magnetic);
	}

	selectX(): Observable<number> {
		return this.x$;
	}

	selectY(): Observable<number> {
		return this.y$;
	}

	getY() {
		return this._y$.getValue();
	}

	getX() {
		return this._x$.getValue();
	}

	getHeight() {
		return this._height$.getValue();
	}

	getWidth() {
		return this._width$.getValue();
	}

	getMaximumLinks(): number {
		return this.maximumLinks;
	}

	setMaximumLinks(maximumLinks: number) {
		this.maximumLinks = maximumLinks;
	}

	getLinkType() {
		return this.linkType;
	}

	setLinkType(type: string) {
		this.linkType = type;
	}

	removeLink(link: LinkModel) {
		const links = this._links$.getValue();
		delete links[link.id];
		this._links$.next({ ...links });
	}

	addLink(link: LinkModel) {
		this._links$.next({ ...this._links$.value, [link.id]: link });
	}

	getLinks(): HashMap<LinkModel> {
		return this._links$.getValue();
	}

	selectLinks(): Observable<HashMap<LinkModel>> {
		return this.links$;
	}

	updateCoords({ x, y, width, height }: { x: number; y: number; width: number; height: number }) {
		this._x$.next(x);
		this._y$.next(y);
		this._width$.next(width);
		this._height$.next(height);
	}

	canLinkToPort(port: PortModel): boolean {
		return true;
	}

	isLocked() {
		return super.getLocked();
	}

	createLinkModel() {
		if (this.getCanCreateLinks()) {
			return new LinkModel();
		}
	}

	destroy() {
		super.destroy();

		Object.values(this.getLinks()).forEach(link => {
			link.destroy();
		});
	}
}

import { BaseAction } from './base.action';
import { DiagramModel } from '../models/diagram.model';
import { Coords } from '../interfaces/coords.interface';

export class SelectingAction extends BaseAction {
	mouseX2: number;
	mouseY2: number;

	dimensions: {
		top: number;
		left: number;
		width: number;
		height: number;
		right: number;
		bottom: number;
	};

	constructor(mouseX: number, mouseY: number) {
		super(mouseX, mouseY);
		this.mouseX2 = mouseX;
		this.mouseY2 = mouseY;
	}

	getBoxDimensions() {
		this.dimensions = {
			left: this.mouseX2 > this.mouseX ? this.mouseX : this.mouseX2,
			top: this.mouseY2 > this.mouseY ? this.mouseY : this.mouseY2,
			width: Math.abs(this.mouseX2 - this.mouseX),
			height: Math.abs(this.mouseY2 - this.mouseY),
			right: this.mouseX2 < this.mouseX ? this.mouseX : this.mouseX2,
			bottom: this.mouseY2 < this.mouseY ? this.mouseY : this.mouseY2
		};
		return this.dimensions;
	}

	containsElement({ x, y }: Coords, diagramModel: DiagramModel): boolean {
		const z = diagramModel.getZoomLevel() / 100.0;
		const dimensions = this.getBoxDimensions();

		return (
			x * z + diagramModel.getOffsetX() > dimensions.left &&
			x * z + diagramModel.getOffsetX() < dimensions.right &&
			y * z + diagramModel.getOffsetY() > dimensions.top &&
			y * z + diagramModel.getOffsetY() < dimensions.bottom
		);
	}
}

