		this.entityId = entity.id;
		this.firing = true;
		this.stopPropagation = () => (this.firing = false);
		this.propogate = options ? options.propagate : null;
	}
}
export class LockEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	locked: boolean;

	constructor(entity: T, locked: boolean = false) {
		super(entity);
		this.locked = locked;
	}
}
export class ParentChangeEvent<P extends BaseEntity = BaseEntity, T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	parent: P;

	constructor(entity: T, parent: P) {
		super(entity);
		this.parent = parent;
	}
}
export class SelectionEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	isSelected: boolean;

	constructor(entity: T, selected: boolean) {
		super(entity);
		this.isSelected = selected;
	}
}
export class PaintedEvent<T extends BaseEntity = BaseEntity> extends BaseEvent<T> {
	isPainted: boolean;

	constructor(entity: T, painted: boolean = false) {
		super(entity);
		this.isPainted = painted;
	}
}
// endregion

import { Directive, EmbeddedViewRef, Input, TemplateRef, ViewContainerRef } from '@angular/core';

class TemplateVarContext {
	$implicit: any;
	templateVar: any;
}

@Directive({
	selector: '[templateVar]',
})
export class TemplateVarDirective {
	view: EmbeddedViewRef<any>;

	protected context = new TemplateVarContext();

	@Input()
	set templateVar(context: any) {
		this.context.$implicit = this.context.templateVar = context;
		this.updateView();
	}

	constructor(protected vcRef: ViewContainerRef, protected templateRef: TemplateRef<any>) {}

	updateView() {
		if (!this.view) {
			this.vcRef.clear();
			this.view = this.vcRef.createEmbeddedView(this.templateRef, this.context);
		} else {
			this.view.markForCheck();
		}
	}
}

