    // data attributes
    this.renderer.setAttribute(rootNode, 'data-portid', model.id);
    this.renderer.setAttribute(rootNode, 'data-name', model.getName());

    model.in
      ? this.renderer.addClass(rootNode, 'in')
      : this.renderer.addClass(rootNode, 'out');

    // assign all passed properties to node initialization.
    Object.entries(model).forEach(([key, value]) => {
      componentRef.instance[key] = value;
    });

    model.onEntityDestroy().subscribe(() => {
      componentRef.destroy();
    });

    componentRef.instance.ngOnInit();
    return componentRef;
  }

  getRecipe(): ComponentFactory<DefaultPortComponent> {
    return this.resolver.resolveComponentFactory(DefaultPortComponent);
  }
}

import { Directive, EmbeddedViewRef, Input, TemplateRef, ViewContainerRef } from '@angular/core';

class TemplateVarContext {
  $implicit: any;
  templateVar: any;
}

@Directive({
  selector: '[templateVar]'
})
export class TemplateVarDirective {
  view: EmbeddedViewRef<any>;

  protected context = new TemplateVarContext();

  @Input()
  set templateVar(context: any) {
    this.context.$implicit = this.context.templateVar = context;
    this.updateView();
  }

  constructor(protected vcRef: ViewContainerRef, protected templateRef: TemplateRef<any>) {}

  updateView() {
    if (!this.view) {
      this.vcRef.clear();
      this.view = this.vcRef.createEmbeddedView(this.templateRef, this.context);
    } else {
      this.view.markForCheck();
    }
  }
}

