.ngdx-diagram {
  position: relative;
  flex-grow: 1;
  display: flex;
  cursor: move;
  overflow: hidden;

  .selector {
    position: absolute;
    background-color: rgba(0, 192, 255, 0.2);
    border: solid 2px rgb(0, 192, 255);
  }

  .ngdx-nodes-layer {
    position: absolute;
    height: 100%;
    width: 100%;
    transform-origin: 0 0;
    overflow: visible !important;
    pointer-events: none;
    z-index: 150;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
  }

  .ngdx-links-layer {
    position: absolute;
    height: 100%;
    width: 100%;
    transform-origin: 0 0;
    overflow: visible !important;
    pointer-events: none;
    z-index: 100;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
  }
}
